package training;

public class DayFour {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//relational operator
		//<,>,<=,>=,!=,==
		System.out.println(88>9);
		System.out.println(88<9);
		System.out.println(88>=9);
		System.out.println(88<=9);
		System.out.println(88==9);
		System.out.println(88!=9);
		
		
		
		//logical operators
		// and &&
		//true && true======>true
		//true && false======>flase
		//false && true======>false
		//false && flase======>false
		
		System.out.println(2==2 && 6!=9);
		System.out.println(2==2 && 6==9);
		System.out.println(2!=2 && 6!=9);
		System.out.println(2!=2&& 6==9);
		
		// or ||
		//true || true ======>true
		//true || false ======>true
		//false || true ======>true
		//flase|| false ======>false
		System.out.println("============================");
		
		System.out.println(4<5 || 7!=8);
		System.out.println(4<5 || 7==8);
		System.out.println(42<5 || 7!=8);
		System.out.println(42<5 || 7==8);
		System.out.println("===================================");
		
		//NOT !
		System.out.println(!(2==2));
		System.out.println(!(2!=2));
		
		
		
		
		
	
		
		
		
	}
	

}
